import { useState } from "react";
import axios from "axios";
//npm install axios
//npm install react-router-dom
import { useNavigate } from 'react-router-dom';
function Register() {
  const [usermsg, setUsermsg] = useState("");  // fillied from response
  const [qrimg, setQrimg] = useState(""); // fillied from response
  const [displayDiv, setDisplayDiv] = useState("none"); // fillied from response
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [email2, setEmail2] = useState("");
  const [password, setPassword] = useState("");
  const [mfaflag, setMfaflag] = useState("false");
  //const [otpoption, setotpoption] = useState("");
  //const navigate = useNavigate();
  const [userRole, setUserRole] = useState("ADMIN_ROLES");
  const [totp, setTotp] = useState("");
  const handleRoleChange = (event) => {
    setUserRole(event.target.value)
  }
  
   const [otpoption, setOtpoption] = useState("0");
   const [otpoptiontext, setOtpoptiontext] = useState("Not required");
   const [isSaveDisabled, setSaveDisabled] = useState(false);
   const [isTotpDisabled, setTotpDisabled] = useState(true);
   
   const onOptionChange = e => {
    setOtpoption(e.target.value);

   
   
    
    //alert(mfaflag);
    //alert(otpoption);
    setMfaflag("true");
if(e.target.value=="0"){
  setOtpoptiontext("Not required.");
  setMfaflag("false");
 
}
else if (e.target.value=="1"){
  setOtpoptiontext("Time-based One Time Password.");
  setMfaflag("true");
}
else{
  setOtpoptiontext("Email One Time Password.");
  setMfaflag("true");
}

  }
  //////////////////
async function handleSubmit(event) {
  event.preventDefault();
  //alert(name + ' ' + userRole + ' '  + email + ' ' + password + ' ' + mfaflag + ' ' + otpoption );
 //navigate('/');
 try {
  const res=await axios.post("http://localhost:9000/api/user",{
    name: name,
    email: email,
    password: password,
    roles:userRole,
    mfaEnabled: mfaflag,
    mfaoption: otpoption
    
})
.then((response) => {
  setUsermsg(response.data.username + " is sucessfully added.");
  if(response.data.qrImageUri != null){
    setQrimg(response.data.qrImageUri);
    setDisplayDiv("block");
    setSaveDisabled(true);
    setTotpDisabled(false);
  }
  else{
    setSaveDisabled(false);
    setTotpDisabled(true);
  }

  //setUsermsg("");
  //setQrimg
  setName("");
  setEmail2(email);
  setEmail("");
  setPassword("");
  setMfaflag("");
  //const [otpoption, setotpoption] = useState("");
  //const navigate = useNavigate();
  setUserRole("");

  
}, (error) => {
  alert(error);
});

//alert(res.data.data);
//alert("Registerd successfully.");
 }
 catch(err){
  alert(err);
  console.log(err);
 }

 }
///////////////////
  //////////////////
  async function handleSendOtp(event) {
    event.preventDefault();
    var code = totp;
   //navigate('/');
   try {
    const res=await axios.post("http://localhost:9000/api/user/validatecode",{
      email: email2,
      code: code
      
  })
  .then((response) => {
    setUsermsg(response.data.username + " is sucessfully activated.");
    if(response.data.qrImageUri != null){
      setQrimg(response.data.qrImageUri);
      setDisplayDiv("block");
    }
    else{
      setDisplayDiv("none");
    }
  
    //setUsermsg("");
    //setQrimg
    setName("");
    setEmail("");
    setPassword("");
    setMfaflag("");
    //const [otpoption, setotpoption] = useState("");
    //const navigate = useNavigate();
    setUserRole("");
  
    
  }, (error) => {
    alert(error);
  });
  
  //alert(res.data.data);
  //alert("Registerd successfully.");
   }
   catch(err){
    alert(err);
    console.log(err);
   }
  
   }
  ///////////////////
    return (

      <div class="container mt-4">
    <div class="row justify-content-center">
        <div class="col-md-6">
            <div class="card">
                <div class="card-body">
                    <h1 class="text-center">User Profile</h1>
                    <form onSubmit={handleSubmit}>
                        <div class="form-group">
                            <label>Name:</label>
                            <input type="text" class="form-control" name="ename" value={name} onChange={(e) => setName(e.target.value)} placeholder="Enter full name" required />
                        </div>
                        <div class="form-group">
                            <label>Role:</label>
                            <select class="form-control" value={userRole} onChange={handleRoleChange}>
                                <option value="ADMIN_ROLES">Admin</option>
                                <option value="MANAGER_ROLES">Manager</option>
                            </select>
                        </div>
                        <div class="form-group">
                            <label>Email:</label>
                            <input type="email" class="form-control" name="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Enter your email" required />
                        </div>
                        <div class="form-group">
                            <label>Password:</label>
                            <input type="password" class="form-control" name="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Enter your password" required />
                        </div>
                        <div class="form-group">
                            <label>Please select the option for 2 Factor Authentication:</label><br />
                            <div class="form-check form-check-inline">
                                <input type="radio" class="form-check-input" name="otpoption" id="0" value="0" checked={otpoption === "0"} onChange={onOptionChange} />
                                <label class="form-check-label" for="0">Not required</label>
                            </div>
                            <div class="form-check form-check-inline">
                                <input type="radio" class="form-check-input" name="otpoption" id="1" value="1" checked={otpoption === "1"} onChange={onOptionChange} />
                                <label class="form-check-label" for="1">Timebased One Time Password</label>
                            </div>
                            <div class="form-check form-check-inline">
                                <input type="radio" class="form-check-input" name="otpoption" id="2" value="2" checked={otpoption === "2"} onChange={onOptionChange} />
                                <label class="form-check-label" for="2">Email One Time Password</label>
                            </div>
                            <p>You have selected <strong>{otpoptiontext}</strong></p>
                        </div>
                        <button type="submit" class="btn btn-primary btn-block" id="save" disabled={isSaveDisabled}>SAVE</button>
                    </form>
                </div>
            </div>
            <div id="rslt" class="mt-3">
                <label>{usermsg}</label>
            </div>
            <div id="qrimgdiv" class="mt-3">
                <img src={qrimg} class="img-fluid" /><br />
                <form onSubmit={handleSendOtp}>
                    <div class="form-group">
                        <label>Time-based OTP:</label>
                        <input type="hidden" class="form-control" name="email2" value={email2} />
                        <input type="text" class="form-control" disabled={isTotpDisabled} name="totp" value={totp} onChange={(e) => setTotp(e.target.value)} required />
                    </div>
                    <button type="submit" class="btn btn-primary" disabled={isTotpDisabled}>Submit</button>
                </form>
            </div>
        </div>
    </div>
</div>

       


    );
  }

  export default Register;
