import React from "react";
import { useState } from "react";
import axios from "axios";
import { useNavigate } from 'react-router-dom';

function Login() {
  const [usermsg, setUsermsg] = useState("");  // fillied from response
  const [otpoption, setOtpoption] = useState("0"); // fillied from response

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  //const [mfaflag, setMfaflag] = useState("false");

  const [totp, setTotp] = useState("");
  const [isLoginDisabled, setLoginDisabled] = useState(false);
  const [isSendOtpDisabled, setSendOtpDisabled] = useState(true);


  /* const onOptionChange = e => {
   setOtpoption(e.target.value);
   
   //alert(mfaflag);
   //alert(otpoption);


 } */

  //////////////////
  async function handleSubmit(event) {
    event.preventDefault();
    var iotp = "0";
    if (otpoption == "") {
      setOtpoption("0");
    }
    if (totp !== "") {
      // alert("inside totp");
      iotp = totp;
    }
    alert("inside " + email + ' ' + password + ' ' + iotp + ' totp: ' + totp);
    try {
      const res = await axios.post("http://localhost:9000/api/user/login", {
        email: email,
        password: password,
        otp: iotp    //otpoption

      })
        .then((response) => {
          alert(response.data.username + ' ' + response.data.otpOption + ' ' + response.data.otp);
          if (response.data.token != null) {
            // alert(response.data.token);
            setUsermsg(response.data.token);
            setLoginDisabled(true);
            setSendOtpDisabled(true);

          }
          if (response.data.otpOption == "1") {
            // alert(response.data.token);
            setUsermsg("Please enter Time-based One Time Password generated in Authenticator App.");
            setLoginDisabled(true);
            setSendOtpDisabled(false);
          }
          if (response.data.otpOption == "2") {
            // alert(response.data.token);
            setUsermsg("Please enter One Time Password sent to your registered Email.Your OTP is " + response.data.otp);
            setLoginDisabled(true);
            setSendOtpDisabled(false);
          }

          //setUsermsg("");
          //setQrimg

          //setEmail("");
          //setPassword("");
          //setMfaflag("");


        }, (error) => {
          // alert(error);
          setUsermsg("Invalid credentials.");
          setLoginDisabled(false);
        });

    }
    catch (err) {
      //alert(err);
      // console.log(err);
      setUsermsg("Invalid credentials.");
    }

  }

  return (
    <div className="container">
        <div className="row">
            <div className="col-sm-9 col-md-7 col-lg-5 mx-auto">
                <div className="card border-0 shadow rounded-3 my-5">
                    <div className="card-body p-4 p-sm-5">
                        <h1 className="card-title text-center mb-5 fw-dark fs-5"><b>Sign In</b></h1>
                        <form onSubmit={handleSubmit} id="loginform">
                            <div className="form-floating mb-3">
                                <input type="email" className="form-control" id="email" placeholder="name@example.com" 
                                value={email} onChange={(e) => setEmail(e.target.value)} required/>
                                <label for="email">Email address</label>
                            </div>
                            <div className="form-floating mb-3">
                                <input type="password" className="form-control" id="pass" placeholder="Password" value={password}
                                onChange={(e) => setPassword(e.target.value)} required/>
                                <label for="pass">Password</label>
                            </div>
                            <div className="d-grid">
                                <button id="login" type="submit" className="btn btn-primary btn-login text-uppercase fw-bold" disabled={isLoginDisabled}>Login</button>
                            </div>
                            <div id="otpField" style="display: none;" className=" my-5">
                                <p>Enter the OTP:</p>
                                <div id="qrimgdiv" className="form-floating mb-3">
                                    <input type="text" className="form-control" disabled={isSendOtpDisabled} name='totp' value={totp}
                                    onChange={(e) => setTotp(e.target.value)}/>
                                    <label for="otpInput">OTP</label>
                                    <div className="d-grid my-3">
                                        <button id="login" className="btn btn-secondary btn-login text-uppercase fw-bold" type="submit"
                                        onclick="handleSendOtp" disabled={isSendOtpDisabled}>Verify Code</button>
                                    </div>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    </div>
  );
}

export default Login;
