import React, { useEffect } from "react";
import { useState } from "react";
import axios from "axios";

//import { useNavigate } from 'react-router-dom';

function Repository() {
  const [usermsg, setUsermsg] = useState("");  // fillied from response
  const [userRole, setUserRole] = useState("ADMIN_ROLES");
    
  const [fname, setFname] = useState("");
  const [file, setFile] = useState()

  function handleFileChange(event) {
    setFile(event.target.files[0])
  }

  const handleNameChange = (event) => {
      setFname(event.target.value)
  }

  const handleRoleChange = (event) => {
    setUserRole(event.target.value)
  }
  const [data, setData] = useState([]);

  useEffect(()=>{
    //alert("inside");
        axios.get("http://localhost:9001/api/user/file/all")
      .then((response) => (
          setData(response.data)
                          )              
      ,(error) => (
       //alert(error);
       setUsermsg("Could not fetch files from Repository")
       
      ),[] ) }
     
  )
  
   /* const onOptionChange = e => {
    setOtpoption(e.target.value);
    
    //alert(mfaflag);
    //alert(otpoption);


  } */
 
  //////////////////
async function handleSubmit(event) {
  event.preventDefault();
 
  /*try {
    const res=await axios.post("http://localhost:9001/api/user/file/add",{
    filename: fname,
    role:userRole//,
    //file:file
    })
    setUsermsg(res.data);
  } catch(err){
    setUsermsg("Failed to add the file");
  }*/


 //////////////////////////////////////// uploading file
   try {
    const formData = new FormData();
    formData.append('file', file);
    formData.append('filename', fname);
    formData.append('role', userRole);
    const res=await axios.post("http://localhost:9001/api/file/upload",formData)
    setUsermsg(res.data);
   } catch(err){
    console.log(err)
    setUsermsg("Failed to add file to Repository")
   }

 //////////////////////////////////////// end of uploading file
  }
///////////////////
    return (

      <div className="container mt-4">
      <div className="card text-center">
          <h1 className="card-header">Welcome to Digital Repository</h1>
          <div className="card-body">
              <p className="card-text">&nbsp;</p>
              <div className="alert alert-info" role="alert">
                  {usermsg}
              </div>
              <form id="form1">
                  <table className="table">
                      <thead>
                          <tr>
                              <th scope="col">File Name</th>
                              <th scope="col">Role</th>
                              <th scope="col" colSpan="3">Action</th>
                          </tr>
                      </thead>
                      <tbody>
                          {data.map((dataObj, index) => (
                              <tr key={index}>
                                  <td><a href={dataObj.filepath}>{dataObj.filename}</a></td>
                                  <td>{dataObj.role}</td>
                                  <td>
                                      <button type="button" className="btn btn-primary">Download</button>
                                  </td>
                                  <td>
                                      <button type="button" className="btn btn-primary">Edit</button>
                                  </td>
                                  <td>
                                      <button type="button" className="btn btn-danger">Delete</button>
                                  </td>
                              </tr>
                          ))}
                      </tbody>
                  </table>
              </form>
          </div>
  
          <div className="card-footer text-muted">
              <form onSubmit={handleSubmit} id="docform" encType="multipart/form-data">
                  <table className="table">
                      <thead>
                          <tr>
                              <th scope="col" colSpan="3">Add new file</th>
                          </tr>
                      </thead>
                      <tbody>
                          <tr>
                              <td>File Name</td>
                              <td>:</td>
                              <td>
                                  <input type="text" onChange={handleNameChange} className="form-control"/>
                              </td>
                          </tr>
                          <tr>
                              <td>Select Role</td>
                              <td>:</td>
                              <td>
                                  <select value={userRole} onChange={handleRoleChange} className="form-control">
                                      <option value="ADMIN_ROLES">Admin</option>
                                      <option value="USER_ROLES">User</option>
                                  </select>
                              </td>
                          </tr>
                          <tr>
                              <td>Select File</td>
                              <td>:</td>
                              <td>
                                  <input type="file" onChange={handleFileChange} className="form-control"/>
                              </td>
                          </tr>
                          <tr>
                              <td></td>
                              <td></td>
                              <td>
                                  <button type="submit" className="btn btn-primary">Add File</button>
                              </td>
                          </tr>
                      </tbody>
                  </table>
              </form>
          </div>
      </div>
  </div>
  
    );
}
export default Repository;
