import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from 'react-router-dom';

function Login() {
    const [usermsg, setUsermsg] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [totp, setTotp] = useState("");
    const [isLoginDisabled, setLoginDisabled] = useState(false);
    const [isSendOtpDisabled, setSendOtpDisabled] = useState(true);
    const navigate = useNavigate();

    async function handleSubmit(event) {
        event.preventDefault();
        try {
            const res = await axios.post("http://localhost:9000/api/user/login", {
                email: email,
                password: password,
                otp: totp
            });

            if (res.data.token != null) {
                setUsermsg(res.data.token);
                setLoginDisabled(true);
                setSendOtpDisabled(true);
                navigate('/dashboard'); // Redirect to dashboard on successful login
            } else if (res.data.otpOption === "1" || res.data.otpOption === "2") {
                setUsermsg(res.data.otpOption === "1" ? "Please enter Time-based One Time Password generated in Authenticator App." : "Please enter One Time Password sent to your registered Email.");
                setLoginDisabled(true);
                setSendOtpDisabled(false);
            } else {
                setUsermsg("Invalid credentials.");
                setLoginDisabled(false);
            }
        } catch (error) {
            setUsermsg("Invalid credentials.");
            console.error(error);
        }
    }

    return (
        <div className="container mt-4">
            <div className="card" align="center">
                <h1>User Login</h1>
                <div className="card" align="center">
                    <p>{usermsg}</p>
                </div>
                <form onSubmit={handleSubmit} id="loginform">
                    <label>Email:</label><br />
                    <input type='text' className="form-control" name='email'
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Enter your email" required /><br />
                    <label>Password:</label><br />
                    <input type='password' className="form-control" name='password'
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Enter your password" required />
                    <p>&nbsp;</p>
                    <button type="submit" className="btn btn-primary" disabled={isLoginDisabled}>Login</button>
                    <div id="otpField" className="my-5" style={{ display: isSendOtpDisabled ? 'none' : 'block' }}>
                        <p>Enter the OTP:</p>
                        <div className="form-floating mb-3">
                            <input type="text" className="form-control" id="otpInput" placeholder="Enter OTP"
                                value={totp}
                                onChange={(e) => setTotp(e.target.value)} />
                            <label htmlFor="otpInput">OTP</label>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default Login;
