import React, { useEffect } from "react";
import { useState } from "react";
import axios from "axios";

//import { useNavigate } from 'react-router-dom';

function Repository() {
  const [usermsg, setUsermsg] = useState("");  // fillied from response
  const [userRole, setUserRole] = useState("ADMIN_ROLES");
    
  const [fname, setFname] = useState("");
  const [file, setFile] = useState()

  function handleFileChange(event) {
    setFile(event.target.files[0])
  }

  const handleNameChange = (event) => {
      setFname(event.target.value)
  }

  const handleRoleChange = (event) => {
    setUserRole(event.target.value)
  }
  const [data, setData] = useState([]);

  useEffect(()=>{
    //alert("inside");
        axios.get("http://localhost:9001/api/user/file/all")
      .then((response) => (
          setData(response.data)
                          )              
      ,(error) => (
       //alert(error);
       setUsermsg("Could not fetch files from Repository")
       
      ),[] ) }
     
  )
  
   /* const onOptionChange = e => {
    setOtpoption(e.target.value);
    
    //alert(mfaflag);
    //alert(otpoption);


  } */
 
  //////////////////
async function handleSubmit(event) {
  event.preventDefault();
 
  /*try {
    const res=await axios.post("http://localhost:9001/api/user/file/add",{
    filename: fname,
    role:userRole//,
    //file:file
    })
    setUsermsg(res.data);
  } catch(err){
    setUsermsg("Failed to add the file");
  }*/


 //////////////////////////////////////// uploading file
   try {
    const formData = new FormData();
    formData.append('file', file);
    formData.append('filename', fname);
    formData.append('role', userRole);
    const res=await axios.post("http://localhost:9001/api/file/upload",formData)
    setUsermsg(res.data);
   } catch(err){
    console.log(err)
    setUsermsg("Failed to add file to Repository")
   }

 //////////////////////////////////////// end of uploading file
  }
///////////////////
    return (

      <div className="container mt-4">
    <div className="card text-center">
        <h1 className="card-header">Welcome to Digital Repository</h1>
        <div className="card-body">
            <p className="card-text">&nbsp;</p>
            <div className="alert alert-info" role="alert">
                {usermsg}
            </div>

            <div className="row">
                {data.map((dataObj, index) => (
                    <div className="col-md-4 mb-3" key={index}>
                        <div className="card">
                            <div className="card-body">
                                <h5 className="card-title"><a href={dataObj.filepath}>{dataObj.filename}</a></h5>
                                <p className="card-text">{dataObj.role}</p>
                                <button type="button" className="btn btn-primary mr-2">Download</button>
                                <button type="button" className="btn btn-primary mr-2">Edit</button>
                                <button type="button" className="btn btn-danger">Delete</button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>

        </div>

        <div className="card-footer text-muted">
            <form onSubmit={handleSubmit} id="docform" encType="multipart/form-data">
                <div className="row">
                    <div className="col-md-4">
                        <div className="form-group">
                            <label htmlFor="fileName">File Name:</label>
                            <input type="text" className="form-control" id="fileName" onChange={handleNameChange}/>
                        </div>
                    </div>
                    <div className="col-md-4">
                        <div className="form-group">
                            <label htmlFor="userRole">Select Role:</label>
                            <select className="form-control" id="userRole" value={userRole} onChange={handleRoleChange}>
                                <option value="ADMIN_ROLES">Admin</option>
                                <option value="USER_ROLES">User</option>
                            </select>
                        </div>
                    </div>
                    <div className="col-md-4">
                        <div className="form-group">
                            <label htmlFor="file">Select File:</label>
                            <input type="file" className="form-control-file" id="file" onChange={handleFileChange}/>
                        </div>
                    </div>
                </div>
                <button type="submit" className="btn btn-primary">Add File</button>
            </form>
        </div>
    </div>
</div>

    );
}
export default Repository;
