import React, { PureComponent } from 'react';
import axios from 'axios';

export default class Navbar extends PureComponent {
  state = {
    isAuthenticated: false
  };

  async componentDidMount() {
    try {
      const response = await axios.get('"http://localhost:9000/api/user/login"');
      if (response.data.isAuthenticated) {
        this.setState({ isAuthenticated: true });
      }
    } catch (error) {
      console.error('Error checking authentication status:', error);
    }
  }
  
  handleLogout = () => {
    // Redirect to home page
    window.location.href = '/';
  };

  render() {
    const { isAuthenticated } = this.state;

    return (
      <>
        <nav className="navbar navbar-expand-lg bg-body-tertiary">
          <div className="container-fluid">
            <a className="navbar-brand" href="/">Mini Project</a>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarSupportedContent">
              <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                <li className="nav-item">
                  <a className="nav-link active" aria-current="page" href="/">Home</a>
                </li>
                {!isAuthenticated && (
                  <>
                  <li className="nav-item">
                    <a className="nav-link active" aria-current="page" href="/Login">Login</a>
                  </li>
                  <li className="nav-item">
                    <a className="nav-link active" aria-current="page" href="/Register">Register</a> 
                  </li>
                  </>
                )}
                {isAuthenticated && (
                  <li className="nav-item">
                    <a className="nav-link active" aria-current="page" href="/" onClick={this.handleLogout}>Logout</a> 
                  </li>
                )}
                <li className="nav-item">
                  <a className="nav-link active" aria-current="page" href="/Repository">Repository</a> 
                </li>
              </ul>
            </div>
          </div>
        </nav>
      </>
    );
  }
}
