import { useState } from "react";
import axios from "axios";
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

function Register() {
  const [usermsg, setUsermsg] = useState("");
  const [qrimg, setQrimg] = useState("");
  const [displayDiv, setDisplayDiv] = useState("none");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [mfaflag, setMfaflag] = useState("false");
  const [userRole, setUserRole] = useState("ADMIN_ROLES");
  const [totp, setTotp] = useState("");
  const [otpoption, setOtpoption] = useState("0");
  const [otpoptiontext, setOtpoptiontext] = useState("Not required");
  const [isSaveDisabled, setSaveDisabled] = useState(false);
  const [isTotpDisabled, setTotpDisabled] = useState(true);
  const navigate = useNavigate();

  const handleRoleChange = (event) => {
    setUserRole(event.target.value)
  }

  const onOptionChange = e => {
    setOtpoption(e.target.value);
    setMfaflag(e.target.value !== "0" ? "true" : "false");

    if(e.target.value === "0"){
      setOtpoptiontext("Not required.");
    } else if (e.target.value === "1") {
      setOtpoptiontext("Time-based One Time Password.");
    } else {
      setOtpoptiontext("Email One Time Password.");
    }
  }

  async function handleSubmit(event) {
    event.preventDefault();
    try {
      const res = await axios.post("http://localhost:9000/api/user", {
        name: name,
        email: email,
        password: password,
        roles: userRole,
        mfaEnabled: mfaflag,
        mfaoption: otpoption
      });

      setUsermsg(res.data.username + " is successfully added.");
      if (res.data.qrImageUri != null) {
        setQrimg(res.data.qrImageUri);
        setDisplayDiv("block");
        setSaveDisabled(true);
        setTotpDisabled(false);
      } else {
        setSaveDisabled(false);
        setTotpDisabled(true);
      }

      setName("");
      setEmail("");
      setPassword("");
      setUserRole("");
    } catch (err) {
      alert(err);
      console.log(err);
    }
  }

  async function handleSendOtp(event) {
    event.preventDefault();
    try {
      const res = await axios.post("http://localhost:9000/api/user/validatecode", {
        email: email,
        code: totp
      });

      setUsermsg(res.data.username + " is successfully activated.");
      if (res.data.qrImageUri != null) {ZS
        setQrimg(res.data.qrImageUri);
        setDisplayDiv("block");
      } else {
        setDisplayDiv("none");
      }

      setName("");
      setEmail("");
      setPassword("");
      setUserRole("");
    } catch (err) {
      alert(err);
      console.log(err);
    }
  }

  return (
    <div className="container mt-4">
      <div className="card">
        <div className="card-body">
          <h1 className="card-title">User Profile</h1>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">Name:</label>
              <input type="text" className="form-control" id="name" value={name} onChange={(e) => setName(e.target.value)} placeholder="Enter full name" required />
            </div>
            <div className="mb-3">
              <label htmlFor="role" className="form-label">Role:</label>
              <select className="form-select" id="role" value={userRole} onChange={handleRoleChange}>
                <option value="ADMIN_ROLES">Admin</option>
                <option value="MANAGER_ROLES">Manager</option>
              </select>
            </div>
            <div className="mb-3">
              <label htmlFor="email" className="form-label">Email:</label>
              <input type="email" className="form-control" id="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Enter your email" required />
            </div>
            <div className="mb-3">
              <label htmlFor="password" className="form-label">Password:</label>
              <input type="password" className="form-control" id="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Enter your password" required />
            </div>
            <div className="mb-3">
              <p>Please select the option for 2 Factor Authentication</p>
              <div className="form-check">
                <input type="radio" className="form-check-input" id="otp0" name="otpoption" value="0" checked={otpoption === "0"} onChange={onOptionChange} />
                <label className="form-check-label" htmlFor="otp0">Not required</label>
              </div>
              <div className="form-check">
                <input type="radio" className="form-check-input" id="otp1" name="otpoption" value="1" checked={otpoption === "1"} onChange={onOptionChange} />
                <label className="form-check-label" htmlFor="otp1">Time-based One Time Password</label>
              </div>
              <div className="form-check">
                <input type="radio" className="form-check-input" id="otp2" name="otpoption" value="2" checked={otpoption === "2"} onChange={onOptionChange} />
                <label className="form-check-label" htmlFor="otp2">Email One Time Password</label>
              </div>
              <p>You have selected <strong>{otpoptiontext}</strong></p>
            </div>
            <button type="submit" className="btn btn-primary" disabled={isSaveDisabled}>SAVE</button>
          </form>
        </div>
        <div className="card-footer">
          <label>{usermsg}</label>
        </div>
      </div>
      <div style={{ display: displayDiv }} className="mt-4">
        <img src={qrimg} alt="QR Code" />
        <form onSubmit={handleSendOtp} className="mt-3">
          <div className="mb-3">
            <label htmlFor="totp" className="form-label">Time-based OTP:</label>
            <input type="text" className="form-control" id="totp" disabled={isTotpDisabled} value={totp} onChange={(e) => setTotp(e.target.value)} required />
          </div>
          <button type="submit" className="btn btn-primary" disabled={isTotpDisabled}>Submit</button>
        </form>
      </div>
    </div>
  );
}

export default Register;
