import { useState } from "react";
import axios from "axios";
//npm install axios
//npm install react-router-dom
import { useNavigate } from 'react-router-dom';
function Register() {
  const [usermsg, setUsermsg] = useState("");  // fillied from response
  const [qrimg, setQrimg] = useState(""); // fillied from response
  const [displayDiv, setDisplayDiv] = useState("none"); // fillied from response
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [email2, setEmail2] = useState("");
  const [password, setPassword] = useState("");
  const [mfaflag, setMfaflag] = useState("false");
  //const [otpoption, setotpoption] = useState("");
  //const navigate = useNavigate();
  const [userRole, setUserRole] = useState("ADMIN_ROLES");
  const [totp, setTotp] = useState("");
  const handleRoleChange = (event) => {
    setUserRole(event.target.value)
  }

  const [otpoption, setOtpoption] = useState("0");
  const [otpoptiontext, setOtpoptiontext] = useState("Not required");
  const [isSaveDisabled, setSaveDisabled] = useState(false);
  const [isTotpDisabled, setTotpDisabled] = useState(true);

  const onOptionChange = e => {
    setOtpoption(e.target.value);




    //alert(mfaflag);
    //alert(otpoption);
    setMfaflag("true");
    if (e.target.value == "0") {
      setOtpoptiontext("Not required.");
      setMfaflag("false");

    }
    else if (e.target.value == "1") {
      setOtpoptiontext("Time-based One Time Password.");
      setMfaflag("true");
    }
    else {
      setOtpoptiontext("Email One Time Password.");
      setMfaflag("true");
    }

  }
  //////////////////
  async function handleSubmit(event) {
    event.preventDefault();
    //alert(name + ' ' + userRole + ' '  + email + ' ' + password + ' ' + mfaflag + ' ' + otpoption );
    //navigate('/');
    try {
      const res = await axios.post("http://localhost:9000/api/user", {
        name: name,
        email: email,
        password: password,
        roles: userRole,
        mfaEnabled: mfaflag,
        mfaoption: otpoption

      })
        .then((response) => {
          setUsermsg(response.data.username + " is sucessfully added.");
          if (response.data.qrImageUri != null) {
            setQrimg(response.data.qrImageUri);
            setDisplayDiv("block");
            setSaveDisabled(true);
            setTotpDisabled(false);
          }
          else {
            setSaveDisabled(false);
            setTotpDisabled(true);
          }

          //setUsermsg("");
          //setQrimg
          setName("");
          setEmail2(email);
          setEmail("");
          setPassword("");
          setMfaflag("");
          //const [otpoption, setotpoption] = useState("");
          //const navigate = useNavigate();
          setUserRole("");


        }, (error) => {
          alert(error);
        });

      //alert(res.data.data);
      //alert("Registerd successfully.");
    }
    catch (err) {
      alert(err);
      console.log(err);
    }

  }
  ///////////////////
  //////////////////
  async function handleSendOtp(event) {
    alert("inside send otp");
    event.preventDefault();
    var code = totp;
    alert(email2 + ' ' + code);
    //navigate('/');
    try {
      const res = await axios.post("http://localhost:9000/api/user/validatecode", {
        email: email2,
        code: code

      })
        .then((response) => {
          setUsermsg(response.data.username + " is sucessfully activated.");
          if (response.data.qrImageUri != null) {
            setQrimg(response.data.qrImageUri);
            setDisplayDiv("block");
          }
          else {
            setDisplayDiv("none");
          }

          //setUsermsg("");
          //setQrimg
          setName("");
          setEmail("");
          setPassword("");
          setMfaflag("");
          //const [otpoption, setotpoption] = useState("");
          //const navigate = useNavigate();
          setUserRole("");


        }, (error) => {
          alert(error);
        });

      //alert(res.data.data);
      //alert("Registerd successfully.");
    }
    catch (err) {
      alert(err);
      console.log(err);
    }

  }
  ///////////////////
  return (

    <div class="container mt-4">
      <table align="center"><tr><td>
        <div class="card" >
          <p>&nbsp;</p>
          <h1>User Profile</h1>
          <p>&nbsp;</p>
          <form onSubmit={handleSubmit}>
            <label>Name : </label><br />
            <input type='text' class="from-control" name='ename'
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="enter full name" required /><br />

            <label>Role : </label><br />
            <select value={userRole} onChange={handleRoleChange}>
              <option value="ADMIN_ROLES">Admin</option>
              <option value="MANAGER_ROLES">Manager</option>
            </select><br />
            <label>Email : </label><br />
            <input type='text' class="from-control" name='email'
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="enter your email" required /><br />
            <label>Password : </label><br />
            <input type='password' class="from-control" name='password'
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="enter your password" required />
            <p>&nbsp;</p>
            <label>Please select the option for 2 Factor Authentication</label>&nbsp;<br />
            <input type='radio' class="from-control" name='otpoption' id="0" value="0" checked={otpoption === "0"}
              onChange={onOptionChange} />&nbsp;<label htmlFor="0">Not required</label>&nbsp;
            <input type='radio' class="from-control" name='otpoption' id="1" value="1" checked={otpoption === "1"}
              onChange={onOptionChange} />&nbsp;<label htmlFor="1">Timebased One Time Password</label>&nbsp;
            <input type='radio' class="from-control" name='otpoption' id="2" value="2" checked={otpoption === "2"}
              onChange={onOptionChange} />&nbsp;<label htmlFor="2">Email One Time Password</label>
            <p>
              You have selected <strong>{otpoptiontext}</strong>

            </p>
            <button type="submit" class="btn btn-primary" onclick="" id="save" disabled={isSaveDisabled}>SAVE</button>
          </form>
        </div>
        <div id="rslt">
          <label>{usermsg}</label>
        </div>
        <div id="qrimgdiv" >
          <p>
            <img src={qrimg} /><br />
            <form onSubmit={handleSendOtp}>
              <label>Time-based OTP : </label><br />
              <input type='hidden' class="from-control" name='email2'
                value={email2}
              />
              <input type='text' class="from-control" disabled={isTotpDisabled} name='totp'
                value={totp}
                onChange={(e) => setTotp(e.target.value)}
                required /><br />
              <button type="submit" class="btn btn-primary" onclick="" disabled={isTotpDisabled}>SEND OTP</button>
            </form>
          </p>
        </div>
      </td></tr></table>
    </div>



  );
}

export default Register;
