import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from 'react-router-dom';

function Login() {
    const [usermsg, setUsermsg] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [totp, setTotp] = useState("");
    const [isLoginDisabled, setLoginDisabled] = useState(false);
    const [isSendOtpDisabled, setSendOtpDisabled] = useState(true);
    const navigate = useNavigate();

    async function handleSubmit(event) {
        event.preventDefault();
        try {
            const res = await axios.post("http://localhost:9000/api/user/login", {
                email: email,
                password: password,
                otp: totp
            });
            

            if (res.data.token != null) {
                setUsermsg(res.data.token);
                setLoginDisabled(true);
                setSendOtpDisabled(true);
                //navigate('/dashboard'); // Redirect to dashboard on successful login
            } else if (res.data.otpOption === "1" || res.data.otpOption === "2") {
                setUsermsg(res.data.otpOption === "1" ? "Please enter Time-based One Time Password generated in Authenticator App." : "Please enter One Time Password sent to your registered Email.");
                setLoginDisabled(true);
                setSendOtpDisabled(false);
            } else {
                setUsermsg("Invalid credentials.");
                setLoginDisabled(false);
            }
        } catch (error) {
            setUsermsg("Invalid credentials.");
            console.error(error);
        }
    }

    return (
        <div className="container">
            <div className="row">
                <div className="col-sm-9 col-md-7 col-lg-5 mx-auto">
                    <div className="card border-0 shadow rounded-3 my-5">
                        <div className="card-body p-4 p-sm-5">
                            <h1 className="card-title text-center mb-5 fw-dark fs-5"><b>Sign In</b></h1>
                            <p>{usermsg}</p>
                            <form onSubmit={handleSubmit}>
                                <div className="form-floating mb-3">
                                    <input type="email" className="form-control" id="floatingInput" placeholder="name@example.com"
                                        value={email} onChange={(e) => setEmail(e.target.value)} />
                                    <label htmlFor="floatingInput">Email address</label>
                                </div>
                                <div className="form-floating mb-3">
                                    <input type="password" className="form-control" id="floatingPassword" placeholder="Password"
                                        value={password} onChange={(e) => setPassword(e.target.value)} />
                                    <label htmlFor="floatingPassword">Password</label>
                                </div>
                                <div className="d-grid">
                                    <button id="login" className="btn btn-primary btn-login text-uppercase fw-bold" type="submit" disabled={isLoginDisabled}>Login</button>
                                </div>
                                
                                <div id="otpField" className="my-5" style={{ display: isSendOtpDisabled ? 'none' : 'block' }}>
                                    
                                    <div className="form-floating mb-3">
                                        <input type="text" className="form-control" id="otpInput" placeholder="Enter OTP"
                                            value={totp} onChange={(e) => setTotp(e.target.value)} />
                                        <label htmlFor="otpInput">OTP</label>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Login;
