import React, { useEffect } from "react";
import { useState } from "react";
import axios from "axios";

//import { useNavigate } from 'react-router-dom';

function Repository() {
  const [usermsg, setUsermsg] = useState("");  // fillied from response
  const [userRole, setUserRole] = useState("ADMIN_ROLES");
    
  const [fname, setFname] = useState("");
  const [file, setFile] = useState()

  function handleFileChange(event) {
    setFile(event.target.files[0])
  }

  const handleNameChange = (event) => {
      setFname(event.target.value)
  }

  const handleRoleChange = (event) => {
    setUserRole(event.target.value)
  }
  const [data, setData] = useState([]);

  useEffect(()=>{
    //alert("inside");
        axios.get("http://localhost:9001/api/user/file/all")
      .then((response) => (
          setData(response.data)
                          )              
      ,(error) => (
       //alert(error);
       setUsermsg("Could not fetch files from Repository")
       
      ),[] ) }
     
  )
  
   /* const onOptionChange = e => {
    setOtpoption(e.target.value);
    
    //alert(mfaflag);
    //alert(otpoption);


  } */
 
  //////////////////
async function handleSubmit(event) {
  event.preventDefault();
 
  try {
    const res=await axios.post("http://localhost:9001/api/user/file/add",{
    filename: fname,
    role:userRole//,
    //file:file
    })
    setUsermsg(res.data);
  } catch(err){
    setUsermsg("Failed to add the file");
  }


 //////////////////////////////////////// uploading file
   try {
    const res=await axios.post("http://localhost:9001/api/user/file/upload",{
        filename: fname,
        file:file,
        role:userRole
      
    })
    setUsermsg(res.data);
   } catch(err){
    console.log(err)
    setUsermsg("Failed to add file to Repository")
   }

 //////////////////////////////////////// end of uploading file
  }
///////////////////
    return (

      <div class="container mt-4">
       
      <div class="card" align="center">
      
       
        <p>&nbsp;</p>
        <h1>Welcome to Digital Repository</h1>
        <p>&nbsp;</p>
        <div class="card" align="center">
        <table align="center" width="50%"><tr><td> 
          
          <label>{usermsg}</label>
          
        </td></tr></table>
       </div>
       <p>&nbsp;</p>
        <form  id="form1">
          <table align="center" border="1" width="600">
            <thead>
                <tr>
                    <th>File Name</th>
                    <th>Role</th>
                    <th colSpan="3">Action</th>
                </tr>
            </thead>
            <tbody>
            {data.map((dataObj, index) => (
          <tr key={index}>
            <td width="50%"><a href={dataObj.filepath}>{dataObj.filename}</a></td>
            <td width="40%">{dataObj.role}</td>
            <td>
            <button type="button" class="btn btn-primary" onclick="">Download</button>
            
            </td>
            <td>
            <button type="button" class="btn btn-primary" onclick="">Edit</button>
            
            </td>
            <td>
            
            <button type="button" class="btn btn-primary" onclick="">Delete</button>
            </td>
            
          </tr>
        ))}
            </tbody>
          </table>
          
          </form>
        </div>
       
        <div>
        <p>&nbsp;</p>
        <form onSubmit={handleSubmit} id="docform" encType="multipart/form-data">
          <table align="center" border="1" width="600">
            <thead>
                <tr>
                    <th colSpan="3">Add new file</th>
                    
                </tr>
            </thead>
            <tbody>
            <tr><td> File Name</td><td> :</td>
            <td>
            <input type="text" onChange={handleNameChange}/>
            
            </td>            
          </tr>
          <tr><td>Select Role </td><td> :</td>
            <td>
            <select value={userRole} onChange={handleRoleChange}>
                  <option value="ADMIN_ROLES">Admin</option>
                  <option value="USER_ROLES">User</option>
           </select>
            </td>            
          </tr>
          <tr><td>Select File </td><td> :</td>
            <td>
            <input type="file" onChange={handleFileChange}/>
            </td>            
          </tr>
          <tr><td> </td><td> </td>
            <td>
             <button type="submit" class="btn btn-primary" onclick="">Add File</button>
            </td>            
          </tr>
            </tbody>
          </table>
          
          </form>
        </div>
        
        
        </div>
      


    );
}
export default Repository;
