import { useState } from "react";
import axios from "axios";
import { useNavigate } from 'react-router-dom';

function Register() {
  const [usermsg, setUsermsg] = useState("");
  const [qrimg, setQrimg] = useState("");
  const [displayDiv, setDisplayDiv] = useState("none");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [email2, setEmail2] = useState("");
  const [password, setPassword] = useState("");
  const [mfaflag, setMfaflag] = useState("false");
  const [userRole, setUserRole] = useState("ADMIN_ROLES");
  const [totp, setTotp] = useState("");
  const [otpoption, setOtpoption] = useState("0");
  const [otpoptiontext, setOtpoptiontext] = useState("Not required");
  const [isSaveDisabled, setSaveDisabled] = useState(false);
  const [isTotpDisabled, setTotpDisabled] = useState(true);
  const navigate = useNavigate();

  const handleRoleChange = (event) => {
    setUserRole(event.target.value)
  }

  const onOptionChange = e => {
    setOtpoption(e.target.value);
    setMfaflag("true");
    if (e.target.value === "0") {
      setOtpoptiontext("Not required.");
      setMfaflag("false");
    } else if (e.target.value === "1") {
      setOtpoptiontext("Time-based One Time Password.");
      setMfaflag("true");
    } else {
      setOtpoptiontext("Email One Time Password.");
      setMfaflag("true");
    }
  }

  async function handleSubmit(event) {
    event.preventDefault();
    try {
      const res = await axios.post("http://localhost:9000/api/user", {
        name: name,
        email: email,
        password: password,
        roles: userRole,
        mfaEnabled: mfaflag,
        mfaoption: otpoption
      });
      setUsermsg(response.data.username + " is successfully added.");
      if (response.data.qrImageUri != null) {
        setQrimg(response.data.qrImageUri);
        setDisplayDiv("block");
        setSaveDisabled(true);
        setTotpDisabled(false);
      } else {
        setSaveDisabled(false);
        setTotpDisabled(true);
      }
      setName("");
      setEmail2(email);
      setEmail("");
      setPassword("");
      setMfaflag("");
      setUserRole("");
    } catch (err) {
      alert(err);
      console.log(err);
    }
  }

  async function handleSendOtp(event) {
    event.preventDefault();
    var code = totp;
    try {
      const res = await axios.post("http://localhost:9000/api/user/validatecode", {
        email: email2,
        code: code
      });
      setUsermsg(response.data.username + " is successfully activated.");
      if (response.data.qrImageUri != null) {
        setQrimg(response.data.qrImageUri);
        setDisplayDiv("block");
      } else {
        setDisplayDiv("none");
      }
      setName("");
      setEmail("");
      setPassword("");
      setMfaflag("");
      setUserRole("");
    } catch (err) {
      alert(err);
      console.log(err);
    }
  }

  return (
    <div className="container mt-4">
      <table align="center">
        <tr>
          <td>
            <div className="card">
              <p>&nbsp;</p>
              <h1>User Profile</h1>
              <p>&nbsp;</p>
              <form onSubmit={handleSubmit}>
                <label>Name : </label><br />
                <input type='text' className="form-control" name='ename'
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  placeholder="enter full name" required /><br />

                <label>Role : </label><br />
                <select value={userRole} onChange={handleRoleChange}>
                  <option value="ADMIN_ROLES">Admin</option>
                  <option value="MANAGER_ROLES">Manager</option>
                </select><br />

                <label>Email : </label><br />
                <input type='text' className="form-control" name='email'
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="enter your email" required /><br />

                <label>Password : </label><br />
                <input type='password' className="form-control" name='password'
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="enter your password" required />

                <p>&nbsp;</p>
                <label>Please select the option for 2 Factor Authentication</label>&nbsp;<br />
                <input type='radio' className="form-control" name='otpoption' id="0" value="0" checked={otpoption === "0"}
                  onChange={onOptionChange} />&nbsp;<label htmlFor="0">Not required</label>&nbsp;
                <input type='radio' className="form-control" name='otpoption' id="1" value="1" checked={otpoption === "1"}
                  onChange={onOptionChange} />&nbsp;<label htmlFor="1">Timebased One Time Password</label>&nbsp;
                <input type='radio' className="form-control" name='otpoption' id="2" value="2" checked={otpoption === "2"}
                  onChange={onOptionChange} />&nbsp;<label htmlFor="2">Email One Time Password</label>

                <p>
                  You have selected <strong>{otpoptiontext}</strong>
                </p>

                <button type="submit" className="btn btn-primary" id="save" disabled={isSaveDisabled}>SAVE</button>
              </form>
            </div>

            <div id="rslt">
              <label>{usermsg}</label>
            </div>

            <div id="qrimgdiv" style={{ display: displayDiv }}>
              <p>
                <img src={qrimg} alt="QR Code" /><br />
                <form onSubmit={handleSendOtp}>
                  <label>Time-based OTP : </label><br />
                  <input type='hidden' className="form-control" name='email2'
                    value={email2}
                  />
                  <input type='text' className="form-control" disabled={isTotpDisabled} name='totp'
                    value={totp}
                    onChange={(e) => setTotp(e.target.value)}
                    required /><br />
                  <button type="submit" className="btn btn-primary" disabled={isTotpDisabled}>SEND OTP</button>
                </form>
              </p>
            </div>
          </td>
        </tr>
      </table>
    </div>
  );
}

export default Register;
